let month = ("0" + (date_ob.getMonth() + 1)).slice(-2);

// current year
let year = date_ob.getFullYear();

// current hours
let hours = date_ob.getHours();

// current minutes
let minutes = date_ob.getMinutes();

// current seconds
let seconds = date_ob.getSeconds();

msg.payload=year + "-" + month + "-" + date + " " + hours + ":" + minutes + ":" + seconds+"    "+msg.payload.confidence+"    "+msg.payload.text;
return msg



