let to_false={}
let to_true={}

if(parseFloat(msg.payload.confidence)< global.get("confiance_gate"))
{
    to_false.topic=true
    to_false.payload=msg.payload.text;
}
else{
    to_true.topic=true
    to_true.payload=msg.payload.text;
    to_true.action=msg.payload.options.action;
    to_true.confiance=msg.payload.confidence.toFixed(3);
}

return [to_false,to_true]

