let last_memory=global.get("SWAN_M");
let memory=[];
let now=Date.now();
let first=true

msg={};
if(last_memory.length >0)
{
    console.log("memory avant :");
    console.log(last_memory);
    console.log("nb avant: "+last_memory.length);

    for (i=0; i<last_memory.length;i++)
    {
        if((now-last_memory[i].time)>500 && last_memory[i].attemp>3)
        {
            if(first===true)
            {
                msg.payload=last_memory[i].payload;
                msg.topic=true;
                first=false
            }
            else
            {
                last_memory[i].attemp++;
                memory.push(last_memory[i]); 
            }

        }
        else{
            if(i===0)
            {
                last_memory[i].attemp++;
            }

            memory.push(last_memory[i]); 

        }
    }
    console.log("memory aprés :");
    console.log(memory);
    console.log("nb aprés: "+memory.length)

    global.set("SWAN_M",memory);
    return msg;
}
else{
    global.set("SWAN_M",[]);
}



