if (msg.confiance!=="")
{
    let date_ob = new Date();
    let data={};

    // current date
    // adjust 0 before single digit date
    let date = ("0" + date_ob.getDate()).slice(-2);

    // current month
    let month = ("0" + (date_ob.getMonth() + 1)).slice(-2);

    // current year
    let year = date_ob.getFullYear();

    // current hours
    let hours = date_ob.getHours();

    // current minutes
    let minutes = date_ob.getMinutes();

    // current seconds
    let seconds = date_ob.getSeconds();

    console.log(data)

    msg.payload=year + "-" + month + "-" + date + " " + hours + ":" + minutes + ":" + seconds+"    "+msg.confiance+"    "+msg.payload+"    "+msg.action;
    return msg
}

let accept={}
let reject={}
let search=[]

search.push("test_de_swan")

let find=false

if (msg.topic===true){
    search.forEach((ask) => {
        if(find===false)
        {
            if(msg.action.toString()===ask.toString())
            {
                console.log("jai une correspondance avec : "+ ask)
                find=true
            }
        }
    });

    if(find===true)
    {
        search=[]
        accept.topic=true;
    }
    else
    {
        reject.payload=msg.payload;
        reject.topic=true;
    }
    return [accept,reject]
}

