let find=false

if (msg.topic===true){
    search.forEach((ask) => {
        if(find===false)
        {
            //console.log("ask : "+ask)
            if(msg.action.toString()===ask.toString())
            {
                console.log("jai une correspondance avec : "+ ask)
                find=true
            }
        }
    });

    if(find===true)
    {
        search=[]
        accept.topic=true;
    }
    else
    {
        reject.payload=msg.payload;
        reject.topic=true;
    }
    return [accept,reject]
}

