    search.forEach((ask) => {
        if(find===false)
        {
            if(msg.action.includes(ask))
            {
                console.log("jai une correspondance avec : "+ ask)
                find=true
            }
        }
    });

    if(find===true)
    {
        search=[]
        accept.topic=true;
    }
    else
    {
        reject.payload=msg.payload;
        reject.topic=true;
    }
    return [accept,reject]
}

